from PIL import Image
from numpy import array
import random
import colorsys

X = -1.
Y = -0.
MAX_ZOOM = 0.0000000000004
ITERATIONS = 255

IMG_X = 512
IMG_Y = 512
image = Image.new("RGB", (IMG_X, IMG_Y))


def random_zoom():
	a = random.uniform(1, 9.9999)
	b = random.randint(2, 13)
	return a ** -b


def rgb_conversion(i):
    return (i % 4 * 64, i % 8 * 32, i % 16 * 16)


def rgb_conversion_reddish(i):
    color = 255 * array(colorsys.hsv_to_rgb(i / 255.0, 1.0, 0.5))
    return tuple(color.astype(int))


def mandelbrot():
	zoom = random_zoom()
	x1 = X - zoom
	x2 = X + zoom
	y1 = Y - zoom
	y2 = Y + zoom

	for y in range(IMG_Y):
		zy = y * (y2 - y1) / (IMG_Y - 1) + y1
		for x in range(IMG_X):
			zx = x * (x2 - x1) / (IMG_X - 1) + x1
			z = zx + zy * 1j
			c = z
			for i in range(ITERATIONS):
				if abs(z) > 2.0:
					break
				z = z * z + c
			image.putpixel((x, y), rgb_conversion(i))

	image.save('./images/mandelbrot.png')
